#include <gmock/gmock.h>
#include <chrono>
#include <future>
#include <iostream>
#include "solution.h"

#include "print-time.cpp"

using namespace std;
using namespace ::testing;

typedef Solution Foo;

class LeetCode : public ::testing::Test
{
 public:
  void SetUp() {}

 protected:
  Foo foo;
  const int loopNum = 1e0;
};

TEST_F(LeetCode, test1)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("ababa"), 3);
}
TEST_F(LeetCode, test2)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("aaabaaa"), 6);
}
TEST_F(LeetCode, test3)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("aaabbaaa"), 4);
}
TEST_F(LeetCode, test4)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("aaaaa"), 5);
}
TEST_F(LeetCode, test5)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("abcdef"), 1);
}
TEST_F(LeetCode, test6)
{
  string text = "";
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1(text), 3);
}
TEST_F(LeetCode, test55)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("acbaaa"), 4);
}
TEST_F(LeetCode, test58)
{
  for (int i = 0; i < loopNum; i++) EXPECT_EQ(foo.maxRepOpt1("aabbaba"), 3);
}
